import React from "react";
import { Card, CardContent, CardActions, CardMedia, Typography, Button, Grid } from "@mui/material";

const courses = [
  {
    id: 1,
    title: "Introduction to Computer Science",
    category: "Computer Science",
    description: "A comprehensive introduction to the field of computer science.",
    image: "https://via.placeholder.com/150",
    rating: 4.5,
    price: "$99.99"
  },
  {
    id: 2,
    title: "Civil Engineering Fundamentals",
    category: "Civil Engineering",
    description: "Learn the basic principles and practices of civil engineering.",
    image: "https://via.placeholder.com/150",
    rating: 4.2,
    price: "$89.99"
  },
  {
    id: 3,
    title: "Mechanical Engineering Basics",
    category: "Mechanical Engineering",
    description: "Get started with the fundamentals of mechanical engineering.",
    image: "https://via.placeholder.com/150",
    rating: 4.0,
    price: "$79.99"
  },
  // Add more courses as needed
];

const CourseCard = ({ course }) => {
  return (
    <Card sx={{ maxWidth: 345 }}>
      <CardMedia
        component="img"
        height="140"
        image={course.image}
        alt={course.title}
      />
      <CardContent>
        <Typography gutterBottom variant="h5" component="div">
          {course.title}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          Category: {course.category}
        </Typography>
        <Typography variant="body2" color="text.secondary" sx={{ mt: 2 }}>
          {course.description}
        </Typography>
        <Typography variant="body2" color="text.secondary" sx={{ mt: 2 }}>
          Rating: {course.rating}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          Price: {course.price}
        </Typography>
      </CardContent>
      <CardActions>
        <Button size="small">Enroll</Button>
      </CardActions>
    </Card>
  );
};

const CourseContainer = () => {
  return (
    <Grid container spacing={2}>
      {courses.map((course) => (
        <Grid item key={course.id} xs={12} sm={6} md={4} lg={3}>
          <CourseCard course={course} />
        </Grid>
      ))}
    </Grid>
  );
};

export default CourseContainer;
