import React from 'react';
import { AppBar, Toolbar, Typography, TextField, IconButton } from '@mui/material';
import { Search } from '@mui/icons-material';
import colorConfigs from '../../configs/colorConfigs';
import sizeConfigs from '../../configs/sizeConfigs';

interface TopbarProps {
  searchQuery: any; // Replace `any` with the correct type if possible
  setSearchQuery: (query: any) => void; // Replace `any` with the correct type if possible
}

const Topbar: React.FC<TopbarProps> = ({ searchQuery, setSearchQuery }) => {
  const handleSearchChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSearchQuery(event.target.value);
  };

  return (
    <AppBar
      position="fixed"
      sx={{
        width: `calc(100% - ${sizeConfigs.sidebar.width})`,
        ml: sizeConfigs.sidebar.width,
        boxShadow: 'unset',
        backgroundColor: colorConfigs.topbar.bg,
        color: colorConfigs.topbar.color,
      }}
    >
      <Toolbar>
        <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
          Education Resources Hub
        </Typography>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <TextField
            value={searchQuery}
            onChange={handleSearchChange}
            placeholder="Search any course"
            variant="outlined"
            size="small"
            sx={{ width: '700px' }}
            InputProps={{
              endAdornment: (
                <IconButton size="small">
                  <Search />
                </IconButton>
              ),
            }}
          />
        </div>
      </Toolbar>
    </AppBar>
  );
};

export default Topbar;
