import { useState } from "react";
import { AppBar, Toolbar, TextField, IconButton } from "@mui/material";
import { Search } from "@mui/icons-material";
import colorConfigs from "../../configs/colorConfigs";
import sizeConfigs from "../../configs/sizeConfigs";
import React from "react";
import CourseCard from "./CourseCard"; 
import courses from "./CourseContainer"; 

const Topbar = () => {
  const [searchQuery, setSearchQuery] = useState(""); // State to store the search query
  const [filteredCourses, setFilteredCourses] = useState([]); // State to store filtered courses

  // Function to handle changes in the search input field
  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value); // Update the search query state
    // Filter the courses based on the search query
    const filtered = courses.filter((course) =>
      course.title.toLowerCase().includes(event.target.value.toLowerCase())
    );
    setFilteredCourses(filtered); // Update the filtered courses state
  };

  return (
    <AppBar
      position="fixed"
      sx={{
        width: `calc(100% - ${sizeConfigs.sidebar.width})`,
        ml: sizeConfigs.sidebar.width,
        boxShadow: "unset",
        backgroundColor: colorConfigs.topbar.bg,
        color: colorConfigs.topbar.color
      }}
    >
      <Toolbar sx={{ justifyContent: 'center' }}>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <TextField
            placeholder="Search any course"
            variant="outlined"
            size="small"
            sx={{ width: '700px' }}
            value={searchQuery} // Controlled component: value is bound to searchQuery state
            onChange={handleSearchChange} // Call handleSearchChange function on input change
            InputProps={{
              endAdornment: (
                <IconButton size="small">
                  <Search />
                </IconButton>
              )
            }}
          />
        </div>
      </Toolbar>

      {/* Display the filtered courses */}
      {filteredCourses.map((course) => (
        <CourseCard key={course.id} course={course} />
      ))}
    </AppBar>
  );
};

export default Topbar;
