import { useState } from "react";
import { AppBar, Toolbar, Typography, TextField, IconButton } from "@mui/material";
import { Search } from "@mui/icons-material";
import colorConfigs from "../../configs/colorConfigs";
import sizeConfigs from "../../configs/sizeConfigs";

const Topbar = ({ handleSearch }) => {
  const [searchQuery, setSearchQuery] = useState("");

  const handleSearchChange = (event) => {
    const query = event.target.value;
    setSearchQuery(query);
    handleSearch(query); // Pass the search query to the handleSearch function in the parent component
  };

  return (
    <AppBar
      position="fixed"
      sx={{
        width: `calc(100% - ${sizeConfigs.sidebar.width})`,
        ml: sizeConfigs.sidebar.width,
        boxShadow: "unset",
        backgroundColor: colorConfigs.topbar.bg,
        color: colorConfigs.topbar.color
      }}
    >
      <Toolbar sx={{ justifyContent: 'center' }}>
        <Typography variant="h6">
          Education Resources Hub
        </Typography>
        <div style={{ display: 'flex', alignItems: 'center', marginLeft: 'auto' }}>
          <TextField
            placeholder="Search any course"
            variant="outlined"
            size="small"
            sx={{ width: '300px' }}
            value={searchQuery}
            onChange={handleSearchChange}
            InputProps={{
              endAdornment: (
                <IconButton size="small">
                  <Search />
                </IconButton>
              )
            }}
          />
        </div>
      </Toolbar>
    </AppBar>
  );
};

export default Topbar;
